import React, {useEffect} from "react";
import { BsEmojiSmileFill, BsEmojiFrownFill } from "react-icons/bs";

const ResponseNotification = ({ data, showNotification, setshowNotification }) => {

    useEffect(() => {
        const notificationTimer = setTimeout(() => {
            setshowNotification(false);
        }, 100000);
        return () => {
            clearTimeout(notificationTimer);
        };
    }, []);

    return (
        <>
            {showNotification && (
                    <div className="d-flex text-center py-6">
                        <div className="d-flex w-100 aling-items-center w-full mt-4 mr-4 bg-white rounded shadow p-4">
                            {data.success ? 
                                <>
                                    <div className='mr-4'>
                                        <BsEmojiSmileFill size={24} color="green"/>
                                    </div>
                                    <div className='text-gray-800'>
                                        {data.data}
                                    </div>
                                </>
                                :
                                <>
                                    <div className='mr-4'>
                                        <BsEmojiFrownFill size={24} color="red" />
                                    </div>
                                    <div className='text-gray-800'>
                                        {data.error}
                                    </div>
                                </>
                            }
                        </div>
                    </div>
            )}
        </>
    )
}

export default ResponseNotification;
