import React, { useState, useEffect } from 'react';
import axios from '../api/axios';
import { useForm } from 'react-hook-form';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import { usePromiseTracker, trackPromise } from "react-promise-tracker";
import ResponseNotification from '../Components/ResponseNotification';
import RedirectToPaymentNotification from '../Components/RedirectToPaymentNotification';

const TICKET_URL = '/ticket';

const Home = (props) => {
    
    const [postMessage, setPostMessage] = useState('');
    const [paymentRedirectMessage, setPaymentRedirectMessage] = useState('');
    const [showNotification, setShowNotification] = useState(false);

    const { promiseInProgress } = usePromiseTracker({area: props.area});


    useEffect(() => {
        if(!localStorage.getItem('userToken'))
        {
            const getTokens = async () => {
                const {data: res} = await axios.get('/token');
                localStorage.setItem('userToken', res.token);
            };
            trackPromise(getTokens());
        }
    },[]);

    const {
        register, 
        formState: {errors}, 
        handleSubmit,
    } = useForm({
        mode: "onChange",
        defaultValues: {
            name: "",
            lastname: "",
            token: '',
            quantity: 0,
            payment: 0,
        }
    });

    const postTicketData = async (data) => {
        await axios.post(TICKET_URL, data)
        .then((res) => {
            if(data.payment === '2')
            {
                setPaymentRedirectMessage('Nukreipiame Jus į mokėjimą...');
                getPayment(res.data.orderId);
            }else if(data.payment === '1')
            {
                setShowNotification(true);
                setPostMessage(res.data);
            }
        })
    }

    const onSubmit = (data) => {
        data.token = localStorage.getItem('userToken');
        trackPromise(postTicketData(data));
    }

    const getPayment = async (data) => {
        await axios.get(`/payment/${data}`)
        .then((res) => {
            window.location.href = res.data;
        })
    }

    return(
        <>
            {promiseInProgress &&
                <Box sx={{ 
                    display: 'flex', 
                    justifyContent: 'center', 
                    position: 'absolute', 
                    top: '0', 
                    left: '0', 
                    right: '0', 
                    height: '100%', 
                    alignItems: 'center'
                }}>
                    <CircularProgress />
                </Box>
            }
            {!promiseInProgress &&
                <>
                    <ResponseNotification data={postMessage} showNotification={showNotification} setshowNotification={setShowNotification} />
                    <RedirectToPaymentNotification message={paymentRedirectMessage} />
                    <div className='container d-flex justify-content-center'>
                        <div className="card card-ticket text-center">
                            <div className="card-header">
                                Loterijos bilietas
                            </div>
                            <div className='card-body'>
                                <form onSubmit={handleSubmit(onSubmit)} method="POST">
                                    <div className='form-group'>
                                        <input 
                                            type='text' 
                                            className='form-control' 
                                            placeholder='Įveskite savo vardą'
                                            {...register("name", {required: true})}
                                        />
                                        <div class="invalid-feedback">
                                            {errors.name?.type === "required" && "Vardas yra privalomas"}
                                        </div>
                                    </div>
                                    <div className='form-group'>
                                        <input 
                                            type='text' 
                                            className='form-control' 
                                            placeholder='Įveskite savo pavardę'
                                            {...register("lastname", {required: true})}
                                        />
                                        <div class="invalid-feedback">
                                            {errors.lastname?.type === "required" && "Pavardė yra privaloma"}
                                        </div>
                                    </div>
                                    <p>Pasirinkite bilietų kiekį:</p>
                                    <div className='form-check'>
                                        <input 
                                            className='form-check-input' 
                                            type="radio" 
                                            id="ticket1"
                                            name='quantity' 
                                            value={1}
                                            {...register("quantity", {required: true})}
                                        />
                                        <label className='form-check-label' htmlFor='flexRadio1'>
                                            1 bilietas
                                        </label>
                                    </div>
                                    <div className='form-check'>
                                        <input 
                                            className='form-check-input' 
                                            type="radio" 
                                            id="ticket2"
                                            name='quantity' 
                                            value={5}
                                            {...register("quantity", {required: true})}
                                        />
                                        <label className='form-check-label' htmlFor='flexRadio1'>
                                            5 bilietas
                                        </label>
                                    </div>
                                    <div className='form-check'>
                                        <input 
                                            className='form-check-input' 
                                            type="radio" 
                                            id="ticket2"
                                            name='quantity' 
                                            value={10}
                                            {...register("quantity", {required: true})}
                                        />
                                        <label className='form-check-label' htmlFor='flexRadio1'>
                                            10 bilietas
                                        </label>
                                    </div>
                                    <div className='form-check'>
                                        <input 
                                            className='form-check-input' 
                                            type="radio" 
                                            id="ticket2"
                                            name='quantity' 
                                            value={15}
                                            {...register("quantity", {required: true})}
                                        />
                                        <label className='form-check-label' htmlFor='flexRadio1'>
                                            15 bilietas
                                        </label>
                                    </div>
                                    <div className='form-check'>
                                        <input 
                                            className='form-check-input' 
                                            type="radio" 
                                            id="ticket2"
                                            name='quantity' 
                                            value={20}
                                            {...register("quantity", {required: true})}
                                        />
                                        <label className='form-check-label' htmlFor='flexRadio1'>
                                            20 bilietas
                                        </label>
                                    </div>
                                    <div className='form-check'>
                                        <input 
                                            className='form-check-input' 
                                            type="radio" 
                                            id="ticket2"
                                            name='quantity' 
                                            value={30}
                                            {...register("quantity", {required: true})}
                                        />
                                        <label className='form-check-label' htmlFor='flexRadio1'>
                                            30 bilietas
                                        </label>
                                    </div>
                                    <div className='form-check'>
                                        <input 
                                            className='form-check-input' 
                                            type="radio" 
                                            id="ticket2"
                                            name='quantity' 
                                            value={50}
                                            {...register("quantity", {required: true})}
                                        />
                                        <label className='form-check-label' htmlFor='flexRadio1'>
                                            50 bilietas
                                        </label>
                                    </div>
                                    <div className='form-check'>
                                        <input 
                                            className='form-check-input' 
                                            type="radio" 
                                            id="ticket3"
                                            name='quantity' 
                                            value={100}
                                             {...register("quantity", {required: true})}
                                        />
                                        <label className='form-check-label' htmlFor='flexRadio1'>
                                            100 bilietas
                                        </label>
                                    </div>
                                    <div class="invalid-feedback">
                                            {errors.quantity?.type === "required" && "Bilietų kiekį privaloma pasirinkti"}
                                    </div>
                                    
                                    <p>Pasirinkite mokėjimo būdą:</p>
                                    <div className='form-check'>
                                        <input 
                                            className='form-check-input' 
                                            type="radio"
                                            id="paymentCash"
                                            name='payment' 
                                            value={1}
                                             {...register("payment", {required: true})}
                                        />
                                        <label className='form-check-label' htmlFor='flexRadio1'>
                                            Grynais
                                        </label>
                                    </div>
                                    <div className='form-check'>
                                        <input 
                                            className='form-check-input' 
                                            type="radio"
                                            id="paymentBank"
                                            name='payment' 
                                            value={2}
                                            {...register("payment", {required: true})}
                                        />
                                        <label className='form-check-label' htmlFor='flexRadio1'>
                                            Internetu
                                        </label>
                                    </div>
                                    <div class="invalid-feedback">
                                            {errors.payment?.type === "required" && "Mokėjimo būdą privaloma pasirinkti"}
                                    </div>
                                    <button 
                                        type='submit' 
                                        className='btn btn-primary'
                                    >
                                        Nusipirkti
                                    </button>
                                </form>
                            </div>
                        </div>
                    </div>
                </>
            }
        </>
    )
}

export default Home;
