import React, {useEffect} from "react";

const ResponseNotification = ({ data, showNotification, setshowNotification }) => {

    useEffect(() => {
        const notificationTimer = setTimeout(() => {
            setshowNotification(false);
        }, 10000);

        return () => {
            clearTimeout(notificationTimer);
        };
    }, []);

    return (
        <>
            {showNotification && (
                    <div className="w-25 flex text-center py-6">
                        <div className="flex max-w-xs w-full mt-4 mr-4 bg-white rounded shadow p-4">
                            {data.success ? 
                                <>
                                    <div className='mr-4'>
                                        {/* <img src="/images/success-message.png" alt="success" className="w-6 h-6 text-green-600"/> */}
                                        success
                                    </div>
                                    <div className='flex-1 text-gray-800'>
                                        {data.data}
                                    </div>
                                </>
                                :
                                <>
                                    <div className='mr-4'>
                                        {/* <img src="/images/success-message.png" alt="success" className="w-6 h-6 text-green-600"/> */}
                                        error
                                    </div>
                                    <div className='flex-1 text-gray-800'>
                                        {data.error}
                                    </div>
                                </>
                            }
                        </div>
                    </div>
            )}
        </>
    )
}

export default ResponseNotification;
