import React, { useState, useEffect } from 'react';
import axios from '../api/axios';
import Modal from '../Components/Modal';
import { useForm } from 'react-hook-form';
import Cookies from 'universal-cookie';
import Checkbox from '../Components/Checkbox';
import CircularProgress from '@mui/material/CircularProgress';
import Box from '@mui/material/Box';
import { usePromiseTracker, trackPromise } from "react-promise-tracker";

const TICKET_URL = '/ticket';

const Home = (props) => {
    const [showModal, setShowModal] = useState(false);
    const [postMessage, setPostMessage] = useState('');
    const [childData, setChildData] = useState(null);

    const  [radioState, setRadioState] = useState({
        options: [
            { radioName: "ticketsQuantity", selected: 0 },
            { radioName: "paymentMethod",  selected: 0 },
        ]
    });

    const { promiseInProgress, notWorkingNeedToChange } = usePromiseTracker({area: props.area});

    const cookies = new Cookies();

    useEffect(() => {
        if(!localStorage.getItem('userToken'))
        {
            const getTokens = async () => {
                const {data: res} = await axios.get('/token');
                localStorage.setItem('userToken', res.token);
            };
            trackPromise(getTokens());
        }
    },[]);

    const cookieData = new Date();
    cookieData.setTime(cookieData.getTime() + ( 60 * 10000));

    const {
        register, 
        formState: {errors}, 
        handleSubmit,
    } = useForm({
        mode: "onChange",
        defaultValues: {
            name: "",
            lastname: "",
            token: '',
            quantity: 0,
            payment: 0,
        }
    });

    const postTicketData = async (data) => {
        await axios.post(TICKET_URL, data)
        .then((res) => {
            setPostMessage(res.data.success);
        })
        .catch((err)=>{console.log(err)})
    }

    const onSubmit = (data) => {
        data.token = localStorage.getItem('userToken');
        data.quantity = radioState.options[0].selected;
        console.log(radioState.options[0].selected)
        const userToAdd = {name: data.name, lastname: data.lastname, token: data.token, quantity: data.quantity};
        const prevUsers = cookies.get('users') || [];
        const filteredUser = prevUsers.filter(prev => prev.name !==userToAdd.name || prev.lastname !== userToAdd.lastname);
    
        const newUsers = [...filteredUser, userToAdd];
        cookies.set('users', newUsers, {path: '/', expires: cookieData})

        setShowModal(true);
        trackPromise(postTicketData(data));
    }

    const handleClose = () => {
        setShowModal(false);
    }

    const handleRadioChange = (e) => {
        const { options } = radioState;
        const { name, value } = e.target;
        console.log(name, value)

        const nextState = options.map((option) => {
            if(option.radioName !== name)
            {
                return { ...option };
            } else {
                return { ...option, selected: value};
            }
        });

        setRadioState({ ...radioState, options: nextState});
    }
    
    // const handleRadioSubmit = (e) => {
    //     console.log(radioState);
    // }

    return(
        <>
            {notWorkingNeedToChange &&
                <Box sx={{ 
                    display: 'flex', 
                    justifyContent: 'center', 
                    position: 'absolute', 
                    top: '0', 
                    left: '0', 
                    right: '0', 
                    height: '100%', 
                    alignItems: 'center'
                }}>
                    <CircularProgress />
                </Box>
            }
            {!notWorkingNeedToChange &&
                <>
                    <Modal showModal={showModal} postMessage={postMessage} childData={childData} handleClose={handleClose} promiseInProgress={promiseInProgress}/>
                    <div className='container d-flex justify-content-center'>
                        <div className="card card-ticket text-center">
                            <div className="card-header">
                                Loterijos bilietas
                            </div>
                            <div className='card-body'>
                                <form onSubmit={handleSubmit(onSubmit)} method="POST">
                                    <div className='form-group'>
                                        <input 
                                            type='text' 
                                            className='form-control' 
                                            placeholder='Įveskite savo vardą'
                                            {...register("name", {required: true})}
                                        />
                                        <div class="invalid-feedback">
                                            {errors.name?.type === "required" && "Vardas yra privalomas"}
                                        </div>
                                    </div>
                                    <div className='form-group'>
                                        <input 
                                            type='text' 
                                            className='form-control' 
                                            placeholder='Įveskite savo pavardę'
                                            {...register("lastname", {required: true})}
                                        />
                                        <div class="invalid-feedback">
                                            {errors.lastname?.type === "required" && "Pavardė yra privaloma"}
                                        </div>
                                    </div>
                                    <p>Pasirinkite bilietų kiekį:</p>
                                    <div className='form-check'>
                                        <input 
                                            className='form-check-input' 
                                            type="radio" 
                                            id="ticket1"
                                            name='ticketsQuantity' 
                                            value={1}
                                            onChange={handleRadioChange}
                                            {...register("ticketsQuantity", {required: true})}
                                        />
                                        <label className='form-check-label' htmlFor='flexRadio1'>
                                            1 bilietas
                                        </label>
                                    </div>
                                    <div className='form-check'>
                                        <input 
                                            className='form-check-input' 
                                            type="radio" 
                                            id="ticket2"
                                            name='ticketsQuantity' 
                                            value={5}
                                            onChange={handleRadioChange}
                                            {...register("ticketsQuantity", {required: true})}
                                        />
                                        <label className='form-check-label' htmlFor='flexRadio1'>
                                            5 bilietas
                                        </label>
                                    </div>
                                    <div className='form-check'>
                                        <input 
                                            className='form-check-input' 
                                            type="radio" 
                                            id="ticket3"
                                            name='ticketsQuantity' 
                                            value={100}
                                            onChange={handleRadioChange}
                                            {...register("ticketsQuantity", {required: true})}
                                        />
                                        <label className='form-check-label' htmlFor='flexRadio1'>
                                            100 bilietas
                                        </label>
                                    </div>
                                    <div class="invalid-feedback">
                                            {errors.ticketsQuantity?.type === "required" && "Bilietų kiekį privaloma pasirinkti"}
                                    </div>
                                    {/* <Checkbox labelText={'1 bilietas'} id={'tickets'} value={1} passChildData={setChildData}/>
                                    <Checkbox labelText={'5 bilietas'} id={'tickets'} value={5} passChildData={setChildData}/>
                                    <Checkbox labelText={'10 bilietas'} id={'tickets'} value={10} passChildData={setChildData}/>
                                    <Checkbox labelText={'15 bilietas'} id={'tickets'} value={15} passChildData={setChildData}/>
                                    <Checkbox labelText={'20 bilietas'} id={'tickets'} value={20} passChildData={setChildData}/>
                                    <Checkbox labelText={'30 bilietas'} id={'tickets'} value={30} passChildData={setChildData}/>
                                    <Checkbox labelText={'50 bilietas'} id={'tickets'} value={50} passChildData={setChildData}/>
                                    <Checkbox labelText={'100 bilietas'} id={'tickets'} value={100} passChildData={setChildData}/> */}
                                    
                                    <p>Pasirinkite mokėjimo būdą:</p>
                                    <div className='form-check'>
                                        <input 
                                            className='form-check-input' 
                                            type="radio"
                                            id="paymentCash"
                                            name='paymentMethod' 
                                            value={1}
                                            onChange={handleRadioChange}
                                            {...register("paymentMethod", {required: true})}
                                        />
                                        <label className='form-check-label' htmlFor='flexRadio1'>
                                            Grynais
                                        </label>
                                    </div>
                                    <div className='form-check'>
                                        <input 
                                            className='form-check-input' 
                                            type="radio"
                                            id="paymentBank"
                                            name='paymentMethod' 
                                            value={2}
                                            onChange={handleRadioChange}
                                            {...register("paymentMethod", {required: true})}
                                        />
                                        <label className='form-check-label' htmlFor='flexRadio1'>
                                            Internetu
                                        </label>
                                    </div>
                                    <div class="invalid-feedback">
                                            {errors.paymentMethod?.type === "required" && "Mokėjimo būdą privaloma pasirinkti"}
                                    </div>
                                    {/* <Checkbox labelText={'Grynais'} id={'payment'} value={'cash'} passChildData={setChildData}/>
                                    <Checkbox labelText={'Internetu'} id={'payment'} value={'bank'} passChildData={setChildData}/> */}
                                    <button 
                                        type='submit' 
                                        className='btn btn-primary'
                                    >
                                        Nusipirkti
                                    </button>
                                </form>
                            </div>
                        </div>
                    </div>
                </>
            }
        </>
    )
}

export default Home;
